mapper.select:
SELECT p.receipt_number as id,detail_request.`text`,detail_request.dept_id asdeptId, detail_request.doctor_id as doctorId ,detail_request.fee_item as feeItem, detail_request.fee_final as feeFinal , detail_request.fee_favor as feeFavor FROM (SELECT request_charge_info.fee_item, request_charge_info.`request_id`, request_charge_info.fee_final, request_charge_info.fee_favor, charge_request.patient_id, charge_request.`doctor_id`, charge_request.dept_id, dict_request_type.`text` FROM request_detail LEFT JOIN request_charge_info ON request_detail.`request_id` = request_charge_info.`request_id` LEFT JOIN charge_request ON request_detail.`patient_id` = charge_request.`request_id` LEFT JOIN dict_request_type ON charge_request.`request_type` = dict_request_type.`id`) detail_request, (SELECT charge_receipt.receipt_number, record_charge.`patient_id` FROM charge_receipt LEFT JOIN record_charge ON charge_receipt.`charge_id` = record_charge.`id` LEFT JOIN charge_pay ON record_charge.id = charge_pay.`charge_id`) p WHERE detail_request.patient_id = p.patient_id
AND p.receipt_number = #{receiptId}
AND detail_request.request_id = #{requestId}
GROUP BY detail_request.request_id
