mapper.select:
SELECT r.id as id, c.text AS costCategory, drt.text AS requestType,ifnull(sum(p.fee_refund),0) as feeRefund , r.create_time AS createTime, r.paid_time AS paidTime, ifnull(rci.chargeable, 0 ) as chargeable, ifnull(rci.discountCost, 0 ) as discountCost, ifnull(rci.actuallyPay, 0 ) as actuallyPay, r.toll_collector_id AS tollCollectorId, cr.dept_id AS deptId, GROUP_CONCAT(distinct dct.text ) AS paymentMethod FROM record_charge r LEFT JOIN category c ON c.category_id = r.category_id LEFT JOIN charge_request cr ON cr.charge_id = r.id LEFT JOIN dict_request_type drt ON drt.id = cr.request_type LEFT JOIN charge_pay p ON p.charge_id = r.id LEFT JOIN dict_channel_type dct ON dct.id = p.channel_type_id LEFT JOIN request_detail rd ON rd.request_id = cr.request_id LEFT JOIN ( SELECT request_id, ifnull(sum(fee_item ),0) AS chargeable, ifnull(sum( fee_favor ),0) AS discountCost, ifnull(sum( fee_final ),0) AS actuallyPay FROM request_charge_info GROUP BY request_id ) rci ON rci.request_id = rd.request_id WHERE 1 = 1
AND r.create_time >= #{createTime}
AND r.paid_time <= #{paidTime}
AND r.toll_collector_id=#{tollCollectorId}
AND r.turn_status =#{turnStatus}
AND drt.text =#{requestType}
AND cr.dept_id =#{deptId}
AND dct.text =#{paymentMethod}
GROUP BY r.id

SELECT a.requestType as requestType, count( a.requestType ) as requestTypeCount, ifnull(sum(chargeable ),0) as sumChargeable, ifnull(sum(discountCost ),0) as sumDiscountCost, ifnull(sum(actuallyPay ),0) as sumActuallyPay FROM ( SELECT c.text AS costCategory, drt.text AS requestType, r.create_time AS createTime, r.paid_time AS paidTime, rci.chargeable AS chargeable, rci.discountCost, rci.actuallyPay, r.toll_collector_id AS tollCollectorId, cr.dept_id AS deptId, GROUP_CONCAT(distinct dct.text ) AS paymentMethod FROM record_charge r LEFT JOIN category c ON c.category_id = r.category_id LEFT JOIN charge_request cr ON cr.charge_id = r.id LEFT JOIN dict_request_type drt ON drt.id = cr.request_type LEFT JOIN charge_pay p ON p.charge_id = r.id LEFT JOIN dict_channel_type dct ON dct.id = p.channel_type_id LEFT JOIN request_detail rd ON rd.request_id = cr.request_id LEFT JOIN ( SELECT request_id, ifnull(sum(fee_item ),0) AS chargeable, ifnull(sum(fee_favor ),0) AS discountCost, ifnull(sum(fee_final),0) AS actuallyPay FROM request_charge_info GROUP BY request_id ) rci ON rci.request_id = rd.request_id
WHERE 1 = 1
AND r.create_time >= #{createTime}
AND r.paid_time <= #{paidTime}
AND r.toll_collector_id=#{tollCollectorId}
AND r.turn_status =#{turnStatus}
AND drt.text =#{requestType}
AND cr.dept_id =#{deptId}
AND dct.text =#{paymentMethod}
GROUP BY r.id ) a WHERE 1 = 1 GROUP BY a.requestType
